CREATE TABLE IF NOT EXISTS film (
    `imdb_id` VARCHAR(10) CHARACTER SET utf8,
    `title` VARCHAR(49) CHARACTER SET utf8,
    `popular_rank` INT,
    `certificate` VARCHAR(5) CHARACTER SET utf8,
    `startYear` INT,
    `endYear` INT,
    `episodes` INT,
    `runtime` VARCHAR(3) CHARACTER SET utf8,
    `type` VARCHAR(12) CHARACTER SET utf8,
    `orign_country` VARCHAR(14) CHARACTER SET utf8,
    `language` VARCHAR(9) CHARACTER SET utf8,
    `plot` VARCHAR(232) CHARACTER SET utf8,
    `summary` VARCHAR(1749) CHARACTER SET utf8,
    `rating` NUMERIC(2, 1),
    `numVotes` INT,
    `genres` VARCHAR(26) CHARACTER SET utf8,
    `isAdult` INT,
    `cast` VARCHAR(409) CHARACTER SET utf8,
    `image_url` VARCHAR(154) CHARACTER SET utf8
);
